  #{staffbirthday,jdbcType=DATE}, #{staffiide,jdbcType=VARCHAR}, #{stafftel,jdbcType=VARCHAR}, 
  #{staffcode,jdbcType=VARCHAR}, #{staffimg,jdbcType=VARCHAR}, #{staffaddress,jdbcType=VARCHAR}, 
  #{staffdepartmentid,jdbcType=INTEGER}, #{staffpassword,jdbcType=VARCHAR}, #{staffroleid,jdbcType=INTEGER}
